#!/bin/sh

install_test_foo() {
    vault kv put secret/test-foo my-value=from_vault_sn0rt
    vault kv patch secret/test-foo TEST_USERNAME=from_vault_true_username
    vault kv patch secret/test-foo TEST_PASSWORD=from_vault_true_password
    vault kv patch secret/test-foo TEST_DIST_LINUX=from_vault_Gentoo
    vault kv patch secret/test-foo MYSQL_PASSWD=from_vault_mysql_password
    vault kv patch secret/test-foo MYSQL_USER=from_vault_mysql_user
    vault kv patch secret/test-foo ACCESS_KEY=from_vault_ac_123123123
    vault kv patch secret/test-foo SECRET_KEY=from_vault_sc_321321321
    vault kv patch secret/test-foo TLS_
    vault kv patch secret/test-foo TLS_
    vault kv get secret/test-foo
}

install_approle_foo() {
  export APPROLE_NAME="approle1"
  export SECRET_PATH="secret/my-secret-approle1"

  vault auth enable approle
  vault write auth/approle/role/$APPROLE_NAME \
      token_ttl=1h \
      token_max_ttl=4h \
      policies="default"

POLICY_NAME="${APPROLE_NAME}-policy"
vault policy write $POLICY_NAME - << EOF
path "$SECRET_PATH" {
  capabilities = ["read"]
}
EOF

vault write auth/approle/role/$APPROLE_NAME \
    policies="default,$POLICY_NAME"
}

install_test_foo
install_approle_foo